// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

interface IHealthConsent {
    function getConsent(address _patient) external view returns (bool, uint256, string memory);
}

contract MedicalRecords {
    // Reference to the HealthConsent contract
    IHealthConsent public healthConsent;

    // Event emitted when a record is uploaded
    event RecordUploaded(address indexed patient, uint256 timestamp, string encryptedData);

    // Mapping to store uploaded records (optional, for retrieval)
    mapping(address => string[]) private patientRecords;

    constructor(address _healthConsentAddress) {
        require(_healthConsentAddress != address(0), "Invalid consent contract address");
        healthConsent = IHealthConsent(_healthConsentAddress);
    }

    // Upload encrypted medical record
    function uploadRecord(address _patient, string memory _encryptedData) public returns (bool) {
        // Verify consent exists
        (bool hasSigned, , ) = healthConsent.getConsent(_patient);
        require(hasSigned, "Consent not signed");
        require(_patient != address(0), "Invalid patient address");
        require(bytes(_encryptedData).length > 0, "Invalid encrypted data");

        // Store encrypted data (optional, can be removed for gas efficiency)
        patientRecords[_patient].push(_encryptedData);

        // Emit event for auditability
        emit RecordUploaded(_patient, block.timestamp, _encryptedData);
        return true;
    }

    // Retrieve patient's uploaded records (optional, for verification)
    function getRecords(address _patient) public view returns (string[] memory) {
        return patientRecords[_patient];
    }
}
